import jsPDF from "jspdf";
import "./IRANSansWeb-normal.utils";
import autoTable from "jspdf-autotable";
import {store} from "../redux/store";
import {itemsDrawers} from "./items-drawers.utils";
import moment from "jalali-moment";
import axios from "axios";

export type HeadersPdf = string[][];

export type DataPdf = any[][];

export const exportPdf = async (
    header: HeadersPdf,
    data: DataPdf,
    company: string,
    title: string = "",
    description: string = "",
    info: string = ""
) => {
  enum Role {
    CMR = "گزارش جامع",
    SUR = "خلاصه گزارشات",
  }

  const states = store.getState()


  const currentTab = states.currentTab;
  const currentPage = states.currentPage;
  const currentTitle = itemsDrawers[currentTab].find((item: any) => item.id === currentPage)?.title;
  const startDate = moment(states.startDate, "YYYY/MM/DD").locale("fa").format("YYYY/MM/DD");
  const endDate = moment(states.endDate, "YYYY/MM/DD").locale("fa").format("YYYY/MM/DD");
  const date = moment(Date.now()).locale('fa').format("YYYY-MM-DD HH:mm:ss");

  const doc = new jsPDF("landscape", "px", "a4");
  doc.setFont("IRANSansWeb", "normal");
  const logo =
      "data:image/png;base64,";

  // fisrt way to access logo

  function getBase64(url: any) {
    return axios
        .get(url, {
          headers: {
            Referer: `${states.company.logo}`,
            "Referrer-Policy": "strict-origin-when-cross-origin",
            "Access-Control-Allow-Origin": "*",
          },
          responseType: "arraybuffer",
        })
        .then((response: any) => Buffer.from(response.data, "binary").toString("base64"));
  }

  // second way to access logo
  // console.log('fdfd')
  //
  // axios.get(states.company.logo)
  //     .then(function (response) {
  //       // handle success
  //       console.log(response);
  //     })
  //     .catch(function (error) {
  //       // handle error
  //       console.log(error);
  //     })
  //     .then(function () {
  //       // always executed
  //     });
  //
  // axios
  //     .get(states.company.logo, {
  //       withCredentials: false,
  //
  //       headers: {
  //         crossOrigin: `Anonymous`,
  //         "Access-Control-Allow-Origin": "*",
  //       }
  //     })
  //     .then(function (response: any) {
  //       var reader = new window.FileReader();
  //       reader.readAsDataURL(response.data);
  //       reader.onload = function () {
  //         var imageDataUrl = reader.result;
  //         console.log(imageDataUrl);
  //       };
  //     });

  const roleTab = currentTab === 1 ? Role.CMR : currentTab === 2 ? Role.SUR : null;

  const descriptionText = `(${roleTab})${currentTitle}`.length;
  const porjectText = `نام پروژه: ${states.project.name}`.length;

  const compareSize = (length: number) => {
    if (length > 24) {
      return 3;
    } else if (length === 24) {
      return 13;
    } else {
      return 33;
    }
  };

  const comapareNameProjectLength = (length: number) => {
    if (length > 24) {
      return 3;
    } else if (length === 24) {
      return 13;
    } else {
      return 50;
    }
  }

  const addFooters = (doc: any) => {
    const pageCount = doc.internal.getNumberOfPages();

    doc.setFontSize(8);
    for (var i = 1; i <= pageCount; i++) {
      doc.setPage(i);
      doc.text("صفحه " + String(i) + " از " + String(pageCount), doc.internal.pageSize.width / 2, 425, {
        align: "center",
      });
      doc.text(`IPASOFT ${new Date().getFullYear()}`, doc.internal.pageSize.width / 2, 435, {
        color: "#FF7519",
        align: "center",
      });
    }
  };

  doc.addImage(logo, "png", 540, 18, 50, 50);
  doc.setFontSize(11);
  const middleOfPage = 270;
  // doc.text(company, middleOfPage + company.length / 2 + 3, 30)
  doc.text(`${company} ${title}`, middleOfPage, 28);
  doc.setFontSize(10);
  doc.text(`(${roleTab})${currentTitle}`, middleOfPage + compareSize(descriptionText) / 2, 44);
  doc.text(
      `نام پروژه: ${states.project.name}`,
      middleOfPage + comapareNameProjectLength(porjectText) / 2,
      57
  );
  doc.setFontSize(9);
  doc.text(`از ${startDate}`, 43, 40);
  doc.text(`تا ${endDate}`, 43, 52);
  doc.text(`تاریخ گزارش گیری: ${moment().locale("fa").format("YYYY/M/D")}`, 43, 64);
  autoTable(doc, {
    startY: 70,
    head: header,
    body: data,
    styles: {
      font: "IRANSansWeb",
      fontSize: 7,
      overflow: "linebreak",
      halign: "center",
      valign: "middle",
    },
    theme: "grid",
  });
  doc.setLanguage("fa-IR");

  const name = `${roleTab} - ${currentTitle}-${date}.pdf`;
  addFooters(doc);
  doc.save(name);
};
